import React, { Component } from "react";

export default class SignUp extends Component {
    constructor(props) {
        super(props);
        this.state = {firstName: '',lastName: '',emailId:'',password:''};

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }
   
      
      handleChange = (e) => {
        this.setState({value:event.target.firstName});
        this.setState({value:event.target.lastName});
        this.setState({value:event.target.emailId});
        this.setState({value:event.target.password});
    }

    handleSubmit(event) {
        alert('All data was submitted : ' + this.state.firstName + this.state.lastName + this.state.emailId + this.state.password);
        event.preventDefault();
    }
    //
    // componentDidMountsas() {

    //     const requestOptions = {
    //         method: 'POST',
    //         headers: { 'Content-Type': 'application/json' },
    //         body: JSON.stringify({ firstName:'', lastName:'',emailId:'',password:'' })
    //     };
    //     fetch('https://backend-quiz.herokuapp.com/api/user/signup', requestOptions)
    //         .then(response => response.json())
    //         .then(data => this.setState({ postId: data.id }));
    // }

    render() {
        return (
            <form>
                <h3>Sign Up</h3>

                <div className="form-group">
                    <label>First name</label>
                    <input value={this.state.firstName} onChange={this.handleChange} type="text" className="form-control" placeholder="First name" required />
                </div>

                <div className="form-group">
                    <label>Last name</label>
                    <input  value={this.state.lastName} onChange={this.handleChange} type="text" className="form-control" placeholder="Last name" required />
                </div>

                <div className="form-group">
                    <label>Email address</label>
                    <input value={this.state.emailId} type="email" onChange={this.handleChange} className="form-control" placeholder="Enter email" required />
                </div>

                <div className="form-group">
                    <label>Password</label>
                    <input value={this.state.password} type="password" onChange={this.handleChange} className="form-control" placeholder="Enter password" required />
                </div>
                {/* onClick={this.componentDidMountsas()} */}
                <button type="submit" className="btn btn-primary btn-block">Sign Up</button>
                {/* <p className="forgot-password text-right">
                    Already registered <a>sign in?</a>
                </p> */}
            </form>
        );
    }
}
