import React, { Component } from "react";
export default class Score extends Component {
    constructor(props) {
        super(props);
        this.state = {
            redirect : false,
            userData : "",
            loggedData : JSON.parse(localStorage.getItem('loggedData'))
            };  

            const requestOptions = {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ id:this.state.loggedData[0].id})
            };
            fetch('http://localhost:3002/api/user/scores', requestOptions)
                .then(response => response.json())
                .then(data => {
                    // if(data.status === 200){                 
                        this.setState({
                            userData: data[0].count
                          });
                    // }
                });
    }

    render() {
        
        return (
            <h2>Your Score is {this.state.userData} </h2>      
          
        );
    }
}
