import React, { Component } from "react";

export default class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            emailId: '',
            password:''};
    }

    handleChange = (e) => {
        this.setState({ [e.target.name]: e.target.value });
      }

 
    login = (e) =>  {
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ email:this.state.emailId,password:this.state.password })
        };
        console.log(requestOptions)

        // fetch('https://backend-quiz.herokuapp.com/api/user/login', requestOptions)
        //     .then(response => response.json())
        //     .then(data => 
        //         alert("success")
        //         );
    }
    render() {
        const {emailId,password} = this.state;
        return (
            <form onSubmit={this.login}>
                <h3>Sign In</h3>

                <div className="form-group">
                    <label>Email address</label>
                    <input name ="emailId" value={emailId} onChange={this.handleChange} type="email" id="emailId" className="form-control" placeholder="Enter email" required />
                </div>

                <div className="form-group">
                    <label>Password</label>
                    <input type="password" name="password" value={password} onChange={this.handleChange} className="form-control" placeholder="Enter password"required />
                </div>

                <div className="form-group">
                    <div className="custom-control custom-checkbox">
                        <input type="checkbox" className="custom-control-input" id="customCheck1" />
                        <label className="custom-control-label" htmlFor="customCheck1">Remember me</label>
                    </div>
                </div>

                <button type="submit" className="btn btn-primary btn-block" >Submit</button>
                {/* <p className="forgot-password text-right">
                    Forgot <a href="#">password?</a>
                </p> */}
            </form>
        );
    }
}
